<?php# МОДУЛЬ О СИСТЕМЕ v.2.0# Coded by ShadoW (c) 2013class cnt_Index extends Controller{	# ИНИЦИАЛИЗАЦИЯ	public function init()	{		# Проверка аторизации		if (!Core::$_data['authed']) Helpers::redirect(_CMS_.'auth');	}	# РЕДИРЕКТ	public function act_index(){ Helpers::redirect(_CMS_); }	# ИНФОРМАЦИЯ О ЯДРЕ	public function act_core()	{		View::assign('%page_title', 'Ядро системы');		View::render('modules/info/core');	}	# СПИСОК МОДУЛЕЙ	public function act_modules()	{		View::assign('%page_title', 'Список модулей');		View::render('modules/info/modules', true, array('modules'=>Core::$_data['cms_modules']));	}	# ПЛАТФОРМА	public function act_platform()	{		View::assign('%page_title', 'Платформа');		View::render('modules/info/platform');	}	# PHP Info	public function act_phpinfo()	{		phpinfo();	}}