<?php# ОБЪЕКТ В ВИДЕ МАССИВА v.2.0# Coded by ShadoW (c) 2014defined('_CORE_') or die('Доступ запрещен');class Array_Object implements ArrayAccess{	protected $_array = NULL;	# ПРОВЕРИТЬ НАЛИЧИЕ КЛЮЧА	public function offsetExists($key)	{		return isset($this->_array[$key]);	}	# ПОЛУЧИТЬ КЛЮЧ	public function offsetGet($key)	{		if ($this->offsetExists($key)) return $this->_array[$key];		return false;	}	# ЗАДАТЬ КЛЮЧ	public function offsetSet($key, $value)	{		$this->_array[$key] = $value;		return true;	}	# УДАЛИТЬ КЛЮЧ	public function offsetUnset($key)	{		unset($this->_array[$key]);	}	# В ВИДЕ МАССИВА	public function asArray()	{		return $this->_array;	}	# КОЛИЧЕСВО ЭЛЕМЕНТОВ	public function count()	{		return count($this->_array);	}}